e{               Reset();			   option = '<option>Não foram encontrados estados!</option>';			   $('#estado').html(option).show();           }        });			<?php if($_SESSION['estadoFiltroInfra'] <> '0'){?>				var estado = <?php echo$_SESSION['estadoFiltroInfra'];?>;			$.getJSON('<?php echo $this->config->base_url();?>index.php/estado/listarCidadesComCnpj?estado='+estado, function (dados){				 			   if (dados.length > 0){    				  var option = '<option value=0>Selecione a cidade</option>';				  $.each(dados, function(i, obj){					   if(obj.id == <?php echo$_SESSION['cidadeFiltroInfra'];?>){						option += '<option selected value="'+obj.id+'">'+obj.nome+'</option>';  					  }else{						option += '<option value="'+obj.id+'">'+obj.nome+'</option>';  					  }				  })				  $('#cidade').html(option).show();			   }else{				   Reset();				   option = '<option>Não foram encontradas cidades!</option>';				   $('#cidade').html(option).show();			   }			});					<?php }?>				$( "#estado" ).change(function() {			var estado = $('#estado').val();				$.getJSON('<?php echo $this->config->base_url();?>index.php/estado/listarCidadesComCnpj?estado='+estado, function (dados){				 			   if (dados.length > 0){    				  var option = '<option value=0>Selecione cidade</option>';				  $.each(dados, function(i, obj){					  option += '<option value="'+obj.id+'">'+obj.nome+'</option>';				  })				  $('#cidade').html(option).show();			   }else{				   Reset();				   option = '<option>Não foram encontradas cidades!</option>';				   $('#cidade').html(option).show();			   }			})	});
