<script src="<?php echo $this->config->base_url(); ?>assets/js/jquery.js"></script><script src="<?php echo $this->config->base_url(); ?>assets/js/jquery-ui-1.9.2.custom.min.js"></script><style type="text/css"> ul.timeline {    list-style-type: none;    position: relative;}ul.timeline:before {    content: ' ';    background: #d4d9df;    display: inline-block;    position: absolute;    left: 29px;    width: 2px;    height: 100%;    z-index: 400;}ul.timeline > li {    margin: 20px 0;    padding-left: 20px;}ul.timeline > li:before {    content: ' ';    background: white;    display: inline-block;    position: absolute;    border-radius: 50%;    border: 3px solid #22c0e8;    left: 20px;    width: 20px;    height: 20px;    z-index: 400;} </style><script type="text/javascript">	$(document).ready(function(){			$( "#cnpjRaiz" ).change(function() {			var estado = $('#estado').val();			var cidade = $('#cidade').val();			var cnpjRaiz = $('#cnpjRaiz').val();			$.getJSON('<?php echo $this->config->base_url();?>index.php/cnpj/listarCnpjFilhosJson?cnpjRaiz='+cnpjRaiz+'&estado='+estado+'&cidade='+cidade, function (dados){				 			   if (dados.length > 0){    				  var option = '<option>Selecione cnpj </option>';				  $.each(dados, function(i, obj){					  option += '<option value="'+obj.id+'">'+obj.cnpj+'</option>';				  })				  $('#cnpj').html(option).show();			   }else{				   Reset();				   option = '<option>Não foram encontrados cnpjs!</option>';				   $('#cnpj').html(option).show();			   }			})	});			$( ".dadosInfracao" ).click(function() {		var id = $(this).attr('id');		$.getJSON('<?php echo $this->config->base_url();?>index.php/notificacao/verDadosNotificacao?id='+id, function (dados){			if (dados.length > 0){    				  $.each(dados, function(i, obj){					$('#cnpjRaiz').text('CNPJ Raiz : '+obj.cnpj_raiz);						$('#cnpj').text('CNPJ : '+obj.cnpj);						$('#num_ie').text('Inscrição Estadual : '+obj.num_ie);						if(obj.num_im){						$('#num_im').text('Inscrição Mobiliária : '+obj.num_im);						}else{						$('#num_im').text('Inscrição Mobiliária : Nenhuma');						}										$('#num_lancamento').text('Num. Lançamento ou Débito : '+obj.num_lancamento);						$('#num_processo').text('Num. Processo : '+obj.num_processo);						$('#data_ciencia_br').text('Data Ciência : '+obj.data_ciencia_br);						$('#prazo_br').text('Prazo : '+obj.prazo_br);						$('#relato_infracao').text('Breve Relato da Infração : '+obj.relato_infracao);								  })			   }else{				   			   }		})		});	});		</script>
